import { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("http://localhost:5000/");
        setMessage(res.data.message);
      } catch (err) {
        setMessage("‚ùå Unauthorized or API error");
      }
    };
    fetchData();
  }, []);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500 text-white">
      <div className="bg-white text-gray-800 rounded-2xl shadow-2xl p-10 max-w-md w-full">
        <h2 className="text-2xl font-bold text-center mb-6">üìä Dashboard</h2>
        <p className="text-center text-lg">{message}</p>
      </div>
    </div>
  );
}
