import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import VerifyOTP from "./VerifyOTP";



export default function Register({ onLogin }) {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [userId, setUserId] = useState("");
  const [showOTP, setShowOTP] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!username || !email || !password) {
      setMessage("‚ùå Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß t√™n, email v√† m·∫≠t kh·∫©u!");
      return;
    }
    try {
      const res = await axios.post("http://localhost:5000/api/auth/register", {
        username,
        email,
        password,
      });
      setMessage(res.data.message);
      setUserId(res.data.userId);
      setShowOTP(true);
    } catch (err) {
      setMessage(err.response?.data?.message || "‚ùå ƒêƒÉng k√Ω th·∫•t b·∫°i!");
    }
  };

  const handleVerified = () => {
    setMessage("‚úÖ T√†i kho·∫£n ƒë√£ x√°c th·ª±c! B·∫°n c√≥ th·ªÉ ƒëƒÉng nh·∫≠p.");
    setTimeout(() => navigate("/login"), 1500);
  };

  if (showOTP && userId) {
    return <VerifyOTP userId={userId} onVerified={handleVerified} />;
  }

  return (
    <div className="form-container">
      <h2>üîê ƒêƒÉng k√Ω</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="T√™n ng∆∞·ªùi d√πng"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="M·∫≠t kh·∫©u"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">ƒêƒÉng k√Ω</button>
      </form>
      {message && <p className="mt-4 text-center">{message}</p>}
    </div>
  );
}
