import { useState } from "react";
import axios from "axios";



export default function ResetPassword({ resetToken }) {
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password !== confirm) {
      setMessage("‚ùå M·∫≠t kh·∫©u x√°c nh·∫≠n kh√¥ng kh·ªõp!");
      return;
    }
    if (!password) {
      setMessage("‚ùå Vui l√≤ng nh·∫≠p m·∫≠t kh·∫©u!");
      return;
    }
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/auth/reset-password", {
        reset_token: resetToken,
        password,
      });
      if (res.data.success) {
        setMessage("‚úÖ ƒê·ªïi m·∫≠t kh·∫©u th√†nh c√¥ng! B·∫°n c√≥ th·ªÉ ƒëƒÉng nh·∫≠p l·∫°i.");
      } else {
        setMessage("‚ùå ƒê·ªïi m·∫≠t kh·∫©u th·∫•t b·∫°i!");
      }
    } catch (err) {
      setMessage(err.response?.data?.message || "‚ùå ƒê·ªïi m·∫≠t kh·∫©u th·∫•t b·∫°i!");
    }
    setLoading(false);
  };

  return (
    <div className="form-container">
      <h2>üîë ƒê·∫∑t l·∫°i m·∫≠t kh·∫©u</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="password"
          placeholder="M·∫≠t kh·∫©u m·ªõi"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="X√°c nh·∫≠n m·∫≠t kh·∫©u"
          value={confirm}
          onChange={(e) => setConfirm(e.target.value)}
          required
        />
        <button type="submit" disabled={loading}>{loading ? "ƒêang ƒë·ªïi..." : "ƒê·ªïi m·∫≠t kh·∫©u"}</button>
      </form>
      {/* Kh√¥ng ki·ªÉm tra ƒë·ªô m·∫°nh m·∫≠t kh·∫©u */}
      {message && <p className="mt-4 text-center">{message}</p>}
    </div>
  );
}
