import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Login({ onLogin }) {
  const [loginValue, setLoginValue] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!loginValue || !password) {
      setMessage("‚ùå Vui l√≤ng nh·∫≠p email/username v√† m·∫≠t kh·∫©u!");
      return;
    }
    try {
      // Ch·ªâ truy·ªÅn ƒë√∫ng lo·∫°i: n·∫øu c√≥ @ th√¨ l√† email, c√≤n l·∫°i l√† username
      const isEmail = loginValue.includes("@");
      const res = await axios.post("http://localhost:5000/api/auth/login", {
        email: isEmail ? loginValue : undefined,
        username: !isEmail ? loginValue : undefined,
        password,
      });
      setMessage("‚úÖ ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");
      // Gi·∫£i m√£ token ƒë·ªÉ l·∫•y user info
      const jwtPayload = JSON.parse(atob(res.data.token.split('.')[1]));
      const userObj = {
        username: jwtPayload.username,
        email: jwtPayload.email,
        role: jwtPayload.role
      };
      if (onLogin) onLogin(userObj, res.data.token);
      setTimeout(() => navigate("/profile"), 1000);
    } catch (err) {
      setMessage(err.response?.data?.message || "‚ùå ƒêƒÉng nh·∫≠p th·∫•t b·∫°i!");
    }
  };

  return (
    <div className="form-container">
      <h2>üîê ƒêƒÉng nh·∫≠p</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Email ho·∫∑c Username"
          value={loginValue}
          onChange={(e) => setLoginValue(e.target.value)}
        />
        <input
          type="password"
          placeholder="M·∫≠t kh·∫©u"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <div style={{textAlign: 'right', marginBottom: '8px'}}>
          <a href="/forgot-password" style={{color: '#4b6cb7', textDecoration: 'underline', fontSize: '0.95em'}}>Qu√™n m·∫≠t kh·∫©u?</a>
        </div>
        <button type="submit">ƒêƒÉng nh·∫≠p</button>
      </form>
      <div style={{textAlign: 'center', marginTop: '16px'}}>
        <a href="/register" style={{color: '#182848', textDecoration: 'underline', fontWeight: 'bold'}}>ƒêƒÉng k√Ω t√†i kho·∫£n m·ªõi</a>
      </div>
      {message && <p className="mt-4 text-center">{message}</p>}
    </div>
  );
}
